import React, { Component, useState } from 'react';

import ImageGallery from  'react-image-gallery';
import 'react-image-gallery/styles/css/image-gallery.css';


class Portfolio extends Component { 
  constructor(props)
  {
    super(props);
    this.state = 
    {
      galleryImages : [
        {
          "original": "https://i.imgur.com/l799YqW.png",
          "thumbnail": "https://i.imgur.com/l799YqW.png",
          "description": "Upgrades Shop Menu (WIP)"
        }],
        description : "Project description"
    };

    const {mydata} = this.props.data;

    //console.log(this.props.data[Object.keys(this.props.data)[0]] );
    console.log(mydata);
  }

  handleClick(images, desc)
  {
    this.setState(
      {
        galleryImages: images,
        description : desc
      }
    )
  }

  render() {

    // if(this.props.data){
    //   var projects = this.props.data.projects.map(function(projects){
    //     var projectImage = 'images/portfolio/'+projects.image;
    //     return <div key={projects.title} className="columns portfolio-item">
    //        <div className="item-wrap">
    //         <a href={projects.url} title={projects.title}>
    //            <img alt={projects.title} src={projectImage} />
    //            <div className="overlay">
    //               <div className="portfolio-item-meta">
    //              <h5>{projects.title}</h5>
    //                  <p>{projects.category}</p>
    //               </div>
    //             </div>
    //           <div className="link-icon"><i className="fa fa-link"></i></div>
    //         </a>
    //       </div>
    //     </div>
    //   })
    // }

    if(this.props.data){
      var projects = this.props.data.projects.map((projects) => {
        var projectImage = 'images/portfolio/'+projects.image;
        return <div key={projects.title} className="columns portfolio-item">
           <div className="item-wrap">

            <a title={projects.title} onClick={ () => this.handleClick(projects.images,projects.description)}>
               <img alt={projects.title} src={projects.images[0].original} />
               <div className="overlay">
                  <div className="portfolio-item-meta">
                 <h5>{projects.title}</h5>
                     <p>{projects.shortDesc}</p>
                  </div>
                </div>
              <div className="link-icon"><i className="fa fa-link"></i></div>
            </a>
          </div>
        </div>
      })
    }
    return (
      <section id="portfolio">

      <div className="row">

         <div className="twelve columns collapsed">
            <h1>Sprawd≈∫ pare moich prac:</h1>
            <ImageGallery items={this.state.galleryImages} showPlayButton='false' showBullets='true'/>
            <h1>{this.state.description}</h1>
            <div id="portfolio-wrapper" className="bgrid-quarters s-bgrid-thirds cf">
                {projects}
            </div>
          </div>
      </div>
   </section>
    );
  }
}

export default Portfolio;
